package com.zhilai.master.modules.notice.service;	import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zhilai.master.common.service.CrudService;import com.zhilai.master.modules.notice.dao.NoticeQueueLogDao;import com.zhilai.master.modules.notice.entity.NoticeQueueLog;	/** *  *Title:NoticeQueueLogService *Description:通知发送日志业务层 *@author ChengJiawei *@date 2018年4月23日 上午9:25:09 * */@Service@Transactional(readOnly=true)public class NoticeQueueLogService extends CrudService<NoticeQueueLogDao, NoticeQueueLog> {		@Autowired	private NoticeQueueLogDao noticeQueueLogDao;		@Override	@Transactional(readOnly = false)	public void save(NoticeQueueLog noticeQueueLog){		if (StringUtils.isBlank(noticeQueueLog.getLogid())) {			noticeQueueLog.preInsert();			noticeQueueLogDao.insert(noticeQueueLog);		}else {			noticeQueueLogDao.update(noticeQueueLog);		}	}}