package com.zhilai.master.modules.pay.service;	import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zhilai.master.common.service.CrudService;import com.zhilai.master.modules.pay.dao.PayHandleDao;import com.zhilai.master.modules.pay.entity.PayHandle;	/** *  *Title:PayHandleService *Description:对账异常处理业务层 *@author ChengJiawei *@date 2018年4月23日 下午5:39:38 * */@Service@Transactional(readOnly=true)public class PayHandleService extends CrudService<PayHandleDao, PayHandle> {		@Autowired	private PayHandleDao payHandleDao;		@Override	@Transactional(readOnly = false)	public void save(PayHandle payHandle){		if (StringUtils.isBlank(payHandle.getLogid())) {			payHandle.preInsert();			payHandleDao.insert(payHandle);		}else {			payHandleDao.update(payHandle);		}	}}