package com.zhilai.master.modules.pay.service;	import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.zhilai.master.common.service.CrudService;import com.zhilai.master.modules.pay.dao.PayPaymentInfoDao;import com.zhilai.master.modules.pay.entity.PayPaymentInfo;	/** *  *Title:PayPaymentInfoService *Description:支付平台账单业务层 *@author ChengJiawei *@date 2018年4月23日 上午10:35:46 * */@Service@Transactional(readOnly=true)public class PayPaymentInfoService extends CrudService<PayPaymentInfoDao, PayPaymentInfo> {		@Autowired	private PayPaymentInfoDao payPaymentInfoDao;		@Override	@Transactional(readOnly = false)	public void save(PayPaymentInfo payPaymentInfo){		if (StringUtils.isBlank(payPaymentInfo.getLogid())) {			payPaymentInfo.preInsert();			payPaymentInfoDao.insert(payPaymentInfo);		}else {			payPaymentInfoDao.update(payPaymentInfo);		}	}}