mapper.select:
SELECT 
	,
	ro.office_id AS "officeList.logid"
FROM sys_role a
LEFT JOIN sys_office o ON o.logid = a.office_id
LEFT JOIN sys_user_role ur ON ur.role_id = a.logid
LEFT JOIN sys_user u ON u.logid = ur.user_id
LEFT JOIN sys_role_office ro ON ro.role_id = a.logid
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}

	AND u.logid = #{user.logid}


	AND u.login_name = #{user.loginName}


	AND a.logid != #{logid}


${sqlMap.dsf}
ORDER BY o.code, a.name

mapper.insert:
INSERT INTO sys_role_menu(role_id, menu_id)

	SELECT #{logid}, #{menu.logid}

	FROM dual

INSERT INTO sys_role_office(role_id, office_id)

	SELECT #{logid}, #{office.logid}

	FROM dual
