mapper.select:
SELECT * FROM sys_trade_define where 1=1

	AND logid = #{logid}


	AND plat_code = #{plat_code}


	AND trade_code like CONCAT('%',#{trade_code},'%' )


	AND trade_name like CONCAT('%',#{trade_name},'%' )


	AND trade_state = #{trade_state}


	AND trade_desc like CONCAT('%',#{trade_desc},'%' )

ORDER BY trade_code desc

SELECT * FROM sys_trade_define where 1=1

	AND logid = #{logid}


	AND plat_code = #{plat_code}


	AND trade_code like CONCAT('%',#{trade_code},'%' )


	AND trade_name like CONCAT('%',#{trade_name},'%' )


	AND trade_state = #{trade_state}


	AND trade_desc like CONCAT('%',#{trade_desc},'%' )

ORDER BY trade_code desc

mapper.update:
	UPDATE sys_trade_define SET plat_code = #{plat_code} ,trade_name = #{trade_name} ,trade_desc = #{trade_desc} ,
trade_state = #{trade_state} ,trade_log = #{trade_log} ,pkg_log = #{pkg_log},  use_type= #{use_type} 
 WHERE trade_code = #{trade_code}
