mapper.select:
SELECT 

FROM cms_article a

WHERE a.logid = #{logid}

SELECT
FROM cms_article a
a.del_flag = #{delFlag}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.posid LIKE '%'||#{posid}||'%' '%'+#{posid}+'%' CONCAT('%', #{posid}, '%')
AND (a.category_id = #{category.logid}
or c.parent_ids like '%'||#{category.logid}||'%' '%,'+#{category.logid}+',%' CONCAT('%,', #{category.logid}, ',%') )
AND a.image = #{image}
AND a.create_by = #{createBy.logid}
ORDER BY ${page.orderBy}
ORDER BY a.weight DESC, a.update_date DESC

SELECT 

FROM cms_article a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.weight DESC, a.update_date DESC

mapper.update:
UPDATE cms_article SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE logid = #{logid}

mapper.select:
SELECT 

from cms_article a where 

	logid in (${logid});

mapper.update:
update cms_article set
	 hits = hits+1
 WHERE logid = #{logid}

mapper.select:
select max(c.logid) AS "logid", max(c.name) AS "name", max(cp.logid) AS "parent.logid", max(cp.name) AS "parent.name", count(*) AS "cnt", sum(a.hits) AS "hits", max(a.update_date) as "updateDate", max(o.logid) AS "office.logid", max(o.name) AS "office.name" FROM cms_article a RIGHT JOIN cms_category c ON c.logid = a.category_id JOIN cms_category cp ON c.parent_id = cp.logid JOIN sys_office o ON o.logid = c.office_id
a.del_flag = #{DEL_FLAG_NORMAL} AND c.site_id = #{site.logid}
AND (c.office_id = #{office.logid} OR o.parent_ids like '%'||#{office.logid}||'%' '%'+#{office.logid}+'%' CONCAT('%', #{office.logid}, '%'))
AND a.update_date >= #{beginDate}
AND a.update_date <= #{endDate}
AND (c.logid = #{logid} OR c.parent_ids LIKE '%'||#{logid}||'%' '%'+#{logid}+'%' CONCAT('%', #{logid}, '%'))
group by cp.sort, cp.logid, c.sort, c.logid order by cp.sort, cp.logid, c.sort, c.logid
